properties.code:
// dateUtils.js

// Helper to safely get the string value of an HL7 field
const getHL7FieldValue = (fieldObj, fieldName) => {
  if (!fieldObj || !fieldName) return "";

  try {
    var component = fieldObj[fieldName + ".1"];
    if (component) {
      return component.toString().trim();
    }
    return fieldObj.toString().trim();
  } catch (e) {
    return "";
  }
};

const normalizeDate = (value) => {
  if (!value) return value;

  // Remove non-digits
  var cleaned = value.replace(/\D/g, "");

  // Allow only YYYYMMDD or YYYYMMDDHHMMSS
  if (cleaned.length === 8 || cleaned.length === 14) {
    return cleaned;
  }

  // Otherwise, log and leave untouched
  logger.warn("Invalid date format: " + value);
  return value;
};

// msgField is the object corresponding to msg[segment][field]
const extractComponents = (msgField, fieldName) => {
  const components = [];

  for (var i = 1; i <= 5; i++) {
    if (msgField && msgField[fieldName + "." + i] != null) {
      components.push(msgField[fieldName + "." + i].toString());
    } else {
      components.push("");
    }
  }

  return components.filter((x) => x !== "").join("^");
};

//metadataUtils.js

// Returns the HL7 message type, e.g., "ORU^R01"
const getMessageType = (msg) => {
  if (!msg || !msg["MSH"] || !msg["MSH"]["MSH.9"]) return "";
  const seg = msg["MSH"]["MSH.9"];
  return (
    seg["MSH.9.1"].toString().trim() + "^" + seg["MSH.9.2"].toString().trim()
  );
};

// Counts segments (OBX, NTE, etc.)
const countSegments = (msg, segmentName) => {
  if (!msg || !msg[segmentName]) return 0;
  return Object.keys(msg[segmentName]).length;
};

// nameUtils.js

// Capitalizes first letter of a string (name)
const capitalize = (value) => {
  if (!value) return value;
  const lower = value.toLowerCase();
  return lower[0].toUpperCase() + lower.slice(1);
};

// Normalize a HL7 name field (family, given, middle)
const normalizeName = (fieldName, field) => {
  if (!field) return;

  for (var i = 1; i <= 3; i++) {
    var key = fieldName + "." + i;
    if (field[key]) {
      field[key] = capitalize(field[key].toString());
    }
  }
};

// export default { capitalize, normalizeName };

// obxUtils.js

const extractObxFields = (obx) => {
  //const {
  //  "OBX.3": { "OBX.3.1": nameField = "", "OBX.3.2": codeField = "" } = {},
  //  "OBX.5": { "OBX.5.1": valueField = "" } = {},
  //  "OBX.7": { "OBX.7.1": rangeField = "" } = {},
  //} = obx || {}; // fallback if obx is null/undefined
  //
  // !! The above doesnt work because of an error:
  // "Default values in destructuring declarations are not supported"
  //
  //return [
  //  nameField != null ? nameField.toString() : "",
  //  codeField != null ? codeField.toString() : "",
  //  valueField != null ? valueField.toString() : "",
  //  rangeField != null ? rangeField.toString() : "",
  //];

  obx = obx || {};
  var nameField = "";
  var codeField = "";
  var valueField = "";
  var rangeField = "";

  if (obx["OBX.3"]) {
    if (obx["OBX.3"]["OBX.3.1"] != null)
      nameField = obx["OBX.3"]["OBX.3.1"].toString();
    if (obx["OBX.3"]["OBX.3.2"] != null)
      codeField = obx["OBX.3"]["OBX.3.2"].toString();
  }

  if (obx["OBX.5"]) {
    if (obx["OBX.5"]["OBX.5.1"] != null)
      valueField = obx["OBX.5"]["OBX.5.1"].toString();
  }

  if (obx["OBX.7"]) {
    if (obx["OBX.7"]["OBX.7.1"] != null)
      rangeField = obx["OBX.7"]["OBX.7.1"].toString();
  }

  return [nameField, codeField, valueField, rangeField];
};

const interpretResultStatus = (valueStr, rangeStr) => {
  if (!rangeStr || rangeStr.trim() === "") {
    return "status: UNDEFINED";
  }

  var parts = rangeStr.split("-");
  if (parts.length !== 2) {
    return "status: UNDEFINED";
  }

  var min = parseFloat(parts[0]);
  var max = parseFloat(parts[1]);
  var value = parseFloat(valueStr);

  if (isNaN(min) || isNaN(max) || isNaN(value)) {
    return "status: UNDEFINED";
  }

  if (value < min) return "status: LOW";
  if (value > max) return "status: HIGH";

  return "status: NORMAL";
};
