com.mirth.connect.plugins.javascriptstep.script:
var props = "";

java.lang.System.getProperties().forEach((k, v) => {
	props += k + " -> " + v + "\n";
});

channelMap.put("PROPS", props);

var file = new java.io.File("/opt/connect/conf/mirth.properties");
var fis = new java.io.FileInputStream(file);
var reader = new java.io.BufferedReader(
    new java.io.InputStreamReader(fis)
);

var line;
var out = "";

while ((line = reader.readLine()) !== null) {
    out += line + "\n";
}

reader.close();

// Write to channel map, or to a file if you prefer
channelMap.put("MIRTH_PROPS", out);

