properties.select:
var dbConn;

try {
	dbConn = getConnection();
	logger.info("GOT CONNECTION!");

	var query = "SELECT message FROM test_table ORDER BY id DESC LIMIT 15;";

	var result = dbConn.executeCachedQuery(query);

	return result;
} finally {
	if (dbConn) { 
		dbConn.close();
	}
}

