var allMessages = "";
var idx = 1;

for each (var result in msg.result) {
    var mes = result.message.toString();
    //logger.info("FROM XML: " + mes);
    allMessages += idx++ + ". " + mes + "\n<br/>";
}

channelMap.put("AllMessagesFromXml", allMessages);

var allMessages = "";
var idx = 1;

var json = JSON.parse(XmlUtil.toJson(msg.toString()));

var results = json.results.result;

if (!(results instanceof Array)) {
    results = [results];
}

for (var i = 0; i < results.length; i++) {
    var mes = results[i].message.toString();
    //logger.info("FROM JSON: " + mes);
    allMessages += idx++ + ". " + mes + "\n<br/>";
}

channelMap.put("AllMessagesFromJson", allMessages);

